import React, { useState } from 'react';
import axios from 'axios';

const GptComponent = () => {
  const [text, setText] = useState('');
  const [prediction, setPrediction] = useState('');

  const API_KEY = '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';
  const API_URL = 'https://api.openai.com/v1/engines/davinci-codex/completions';

  const handleChange = (event) => {
    setText(event.target.value);
    console.log(setText);
  };

  const handleKeyPress = async (event) => {
    if (event.key === 'Enter') {
      const prompt = text.trim() + ' ';
      const response = await axios.post(API_URL, {
        prompt: prompt,
        max_tokens: 5,
        n: 1,
        stop: ['\n']
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${API_KEY}`
        }
      });

      setPrediction(response.data.choices[0].text);
      setText('');
    }
  };

  return (
    <div>
      <input type="text" value={text} onChange={handleChange} onKeyPress={handleKeyPress} />
      <div>{prediction}</div>
    </div>
  );
};

export default GptComponent;
