import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GptChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const API_KEY = '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';
  const API_URL = 'https://api.openai.com/v1/engines/davinci-codex/completions';

  useEffect(() => {
    setMessages([{ text: '안녕하세요! 무엇을 도와드릴까요?', isBot: true }]);
  }, []);

  const handleInputChange = (event) => {
    setInput(event.target.value);
  };

  const handleInputKeyPress = async (event) => {
    if (event.key === 'Enter' && input !== '') {
      const userMessage = { text: input, isBot: false };
      setMessages([...messages, userMessage]);
      setInput('');

      const prompt = messages.map((message) => {
        return message.isBot ? `bot: ${message.text}` : `user: ${message.text}`;
      }).join('\n') + `\nbot: `;

      const response = await axios.post(API_URL, {
        prompt: prompt,
        max_tokens: 50,
        n: 1,
        stop: ['\n', 'user:', 'bot:']
      }, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${API_KEY}`
        }
      });

      const botMessage = { text: response.data.choices[0].text.trim(), isBot: true };
      setMessages([...messages, botMessage]);
    }
  };

  return (
    <div>
      <div style={{ height: '400px', overflowY: 'scroll' }}>
        {messages.map((message, index) => (
          <div key={index} style={{ marginBottom: '10px' }}>
            {message.isBot ? <div><b>Bot:</b> {message.text}</div> : <div><b>You:</b> {message.text}</div>}
          </div>
        ))}
      </div>
      <input type="text" value={input} onChange={handleInputChange} onKeyPress={handleInputKeyPress} />
    </div>
  );
};

export default GptChatbot;
